import { HttpHeaderResponse } from '@angular/common/http';
import { Component } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';
import { ActivatedRoute } from '@angular/router';
import { IClient } from 'src/app/interfaces/client';
import { ClientsService } from 'src/app/services/clients.service';
import Swal from 'sweetalert2';

@Component({
  selector: 'app-client-register-update',
  templateUrl: './client-register-update.component.html',
  styleUrls: ['./client-register-update.component.css'],
})
export class ClientRegisterUpdateComponent {
  constructor(private clientsService: ClientsService, private route: ActivatedRoute) {}


  // Resolver o problema do tipo do CPF (string ou number)
  ngOnInit() {
    const cpf = Number(this.route.snapshot.paramMap.get('cpf'));
    if(cpf) {
      this.clientsService.findClientByCpf(cpf).subscribe((client: IClient) => {
        console.log(result);
      });
    }
  }



  registerForm = new FormGroup({
    cpf: new FormControl(0, Validators.required),
    nome: new FormControl('',Validators.required),
    telefone: new FormControl(0,Validators.required),
    logradouro: new FormControl('',Validators.required),
    renda: new FormControl(0, Validators.required),
  });

  cadastrar() {
    const client: IClient = this.registerForm.value as IClient;
    this.clientsService.registerClient(client).subscribe(result => {
      Swal.fire({
        icon: 'success',
        title: 'Cadastrado com sucesso!'
      })
    }, error => {
      console.error();
    });
  }

}
<!-- Componentizar o formulário. -->

<h1 class="display-6">Cadastro e Atualização</h1>
<div>{{registerForm.value | json}}</div>
<div class="container-fluid">
  <form [formGroup]="registerForm" (ngSubmit)="cadastrar()" action="">
    <div class="row">
      <div class="col-3">
        <div class="mb-3">
          <label for="cpf" class="form-label">CPF</label>
          <input type="text" class="form-control" id="cpf" formControlName="cpf" required/>
        </div>
        <div class="mb-3">
          <label for="logradouro" class="form-label">Logradouro</label>
          <input type="text" class="form-control" id="logradouro" formControlName="logradouro"/>
        </div>
        <div class="mb-3">
          <label for="renda" class="form-label">Renda Mensal</label>
          <input type="number" class="form-control" id="renda" formControlName="renda" />
        </div>
      </div>
      <div class="col-3">
        <div class="mb-3">
          <label for="nome" class="form-label">Nome</label>
          <input type="text" class="form-control" id="nome" formControlName="nome"/>
        </div>
        <div class="mb-3">
          <label for="Complemento" class="form-label">Complemento</label>
          <input type="text" class="form-control" id="Complemento"/>
        </div>
      </div>
      <div class="col-3">
        <div class="mb-3">
          <label for="telefone" class="form-label">Telefone</label>
          <input type="number" class="form-control" id="telefone" formControlName="telefone"/>
        </div>
        <div class="mb-3">
          <label for="cep" class="form-label">CEP</label>
          <input type="text" class="form-control" id="cep"/>
        </div>
      </div>
    </div>
    <button type="submit" [disabled]="!registerForm.valid" class="btn btn-primary">Submit</button>
  </form>
</div>
