import { Component } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';
import { ActivatedRoute } from '@angular/router';
import { IClient } from 'src/app/interfaces/client';
import { ClientsService } from 'src/app/services/clients.service';
import Swal from 'sweetalert2';

@Component({
  selector: 'app-client-register-update',
  templateUrl: './client-register-update.component.html',
  styleUrls: ['./client-register-update.component.css'],
})
export class ClientRegisterUpdateComponent {
  registerForm = new FormGroup({
    cpf: new FormControl(0, Validators.required),
    nome: new FormControl('', Validators.required),
    telefone: new FormControl(0, Validators.required),
    logradouro: new FormControl('', Validators.required),
    complemento: new FormControl('', Validators.required),
    cep: new FormControl('', Validators.required),
    rendimentoMensal: new FormControl(0, Validators.required),
  });

  constructor(
    private clientsService: ClientsService,
    private route: ActivatedRoute,
  ) {}

  clientCpf = 0;

  ngOnInit() {
    this.clientCpf = Number(this.route.snapshot.paramMap.get('cpf'));
    if (this.clientCpf) {
      this.clientsService
        .findClientByCpf(this.clientCpf)
        .subscribe((client: IClient) => {
          this.registerForm.setValue({
            cpf: client.cpf,
            nome: client.nome,
            telefone: client.telefone,
            logradouro: client.logradouro,
            complemento: client.complemento,
            cep: client.cep,
            rendimentoMensal: client.rendimentoMensal,
          });
        });
    }
  }

  onSubmit() {
    if (this.clientCpf) {
      this.cadastrar();
    } else {
      this.atualizar();
    }
  }

  cadastrar() {
    const client: IClient = this.registerForm.value as IClient;
    this.clientsService.registerClient(client).subscribe(
      (result) => {
        Swal.fire({
          icon: 'success',
          title: 'Cadastro realizado!'
        }).then(() => {window.open("http://localhost:4200/clientes", "_self")});
      },
      (error) => {
        console.error();
      }
    );
  }

  atualizar() {
    const client: IClient = this.registerForm.value as IClient;
    this.clientsService.updateClient(client, this.clientCpf).subscribe(
      (result) => {
        Swal.fire({
          title: 'Cliente atualizado com sucesso',
          icon: 'success',
          confirmButtonText: 'Ok'
        }).then(() => {window.open("http://localhost:4200/clientes", "_self")});
      },
      (error) => {
        Swal.fire({
          icon: 'error',
          title: 'Oops...',
          text: 'Algo deu errado! Verifique o erro no console.',
        });
        console.error(error);
      }
    );
  }

}
 <div class="container d-flex justify-content-center">
  <div class="panel-body">
  <h1 class="display-6 m-4 fw-bold" style="text-align: center;">{{ clientCpf ? 'Atualizar Cadastro' : 'Cadastrar'}}</h1>
  <div class="panel bg-whit shadow p-4 m-4" style="width: 40em">
      <form [formGroup]="registerForm" (ngSubmit)="onSubmit()" action="">
            <div class="row mb-3">
              <label for="cpf" class="form-label">CPF</label>
              <input type="text" class="form-control" id="cpf" formControlName="cpf" required/>
            </div>
            <div class="row mb-3">
              <label for="logradouro" class="form-label">Logradouro</label>
              <input type="text" class="form-control" id="logradouro" formControlName="logradouro"/>
            </div>
            <div class="row mb-3">
              <label for="renda" class="form-label">Renda Mensal</label>
              <input type="number" class="form-control" id="renda" formControlName="rendimentoMensal"/>
            </div>
            <div class="row mb-3">
              <label for="nome" class="form-label">Nome</label>
              <input type="text" class="form-control" id="nome" formControlName="nome"/>
            </div>
            <div class="row mb-3">
              <label for="Complemento" class="form-label">Complemento</label>
              <input type="text" class="form-control" id="Complemento" formControlName="complemento"/>
            </div>
            <div class="row mb-3">
              <label for="telefone" class="form-label">Telefone</label>
              <input type="number" class="form-control" id="telefone" formControlName="telefone"/>
            </div>
            <div class="row mb-3">
              <label for="cep" class="form-label">CEP</label>
              <input type="text" class="form-control" id="cep" formControlName="cep"/>
            </div>
        <button type="submit" [disabled]="!registerForm.valid" class="btn btn-primary">{{clientCpf ? 'Atualizar' : 'Cadastrar'}}</button>
        <a routerLink="/clientes"><button type="text" class="btn btn-danger ms-2">Voltar</button></a>
      </form>
    </div>
  </div>
 </div>

