import { Injectable } from '@angular/core';
import Swal from 'sweetalert2';

@Injectable({
  providedIn: 'root',
})
export class AlertsService {
  constructor() {}

  alertSuccess(message: string) {
    Swal.fire({
      icon: 'success',
      title: 'Operação realizada com sucesso!',
      text: message,
    });
  }

  alertError(message: string) {
    Swal.fire({
      icon: 'error',
      title: 'Algo deu errado!',
      text: message,
    });
  }


}
