import { Component } from '@angular/core';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent {

}
<div class="px-4 py-5 my-5 text-center">
  <i class="fa-solid fa-user mb-4" style="font-size: 5em;"></i>
  <h1 class="display-5 fw-bold">CRUD Clientes</h1>
  <div class="col-lg-6 mx-auto">
    <p class="lead mb-4">Projeto de Cadastro, Leitura, Edição e Exclusão de Clinetes de uma empresa Financeira de emprestimos. Front-End criado com Angular 15, Back-End criado com Java 8 + Spring Boot e o Banco de Dados H2 em memória. Criado a partir da proposta do teste final do Treinamento Jovens Profissionais.</p>
    <div class="d-grid gap-2 d-sm-flex justify-content-sm-center">
      <button type="button" class="btn btn-warning btn-lg px-4 gap-3" routerLink="/clientes">Clientes</button>
      <a class="btn btn-outline-secondary btn-lg px-4" href="https://github.com/IsaacLouzeiro/consumo-api-emprestimo" target="_blank">Sobre</a>
    </div>
  </div>
</div>
