import { Component } from '@angular/core';
import { IClient } from 'src/app/interfaces/client';
import { ClientsService } from 'src/app/services/clients.service';
import Swal from 'sweetalert2';

@Component({
  selector: 'app-clients',
  templateUrl: './clients.component.html',
  styleUrls: ['./clients.component.css'],
})
export class ClientsComponent {
  clients: IClient[] = [];
  constructor(private clientsService: ClientsService) {}

  ngOnInit() {
    this.showAllClients();
  }

  showAllClients() {
    this.clientsService.listAllClients().subscribe((result: IClient[]) => {
      this.clients = result;
    });
  }

  deleteClient(cpf: number): void {
    Swal.fire({
      title: 'Tem certeza?',
      text: 'Isso não é reversível.',
      icon: 'warning',
      showCancelButton: true,
      confirmButtonColor: '#3085d6',
      cancelButtonColor: '#d33',
      confirmButtonText: 'Sim, deletar!',
      cancelButtonText: 'Cancelar',
    }).then((result) => {
      if (result.isConfirmed) {
        this.clientsService.deleteClientByCpf(cpf).subscribe(() => {
          Swal.fire('Deletado!', 'O cliente foi deletado.', 'success');
          this.ngOnInit();
        });
      }
    });
  }
}
<div class="container">
  <h1 class="display-6 m-4 fw-bold" style="text-align: center">Clientes</h1>
  <a routerLink="/clientes/cadastrar" type="button" class="btn btn-success"
    >Cadastrar Cliente</a
  >
  <table class="table table-bordered table-striped mt-3">
    <thead>
      <tr>
        <th scope="col">CPF</th>
        <th scope="col">Nome</th>
        <th scope="col">Telefone</th>
        <th scope="col">Endereço</th>
        <th scope="col">Complemento</th>
        <th scope="col">CEP</th>
        <th scope="col">Renda Mensal</th>
        <th scope="col">Ações</th>
      </tr>
    </thead>
    <tbody>
      <tr *ngFor="let client of clients">
        <td>{{ client.cpf }}</td>
        <td>{{ client.nome }}</td>
        <td>{{ client.telefone }}</td>
        <td>{{ client.rua }}</td>
        <td>{{ client.complemento }}</td>
        <td>{{ client.cep }}</td>
        <td>{{ client.rendimentoMensal | currency : "BRL" }}</td>
        <td>
          <div class="dropdown">
            <button
              class="btn btn-secondary dropdown-toggle btn-dark"
              type="button"
              data-bs-toggle="dropdown"
              aria-expanded="false"
            >
              Opções
            </button>
            <ul class="dropdown-menu">
              <li>
                <a
                  routerLink="/clientes/atualizar/{{ client.cpf }}"
                  class="dropdown-item"
                  >Alterar Cadastro</a
                >
              </li>
              <li>
                <a class="dropdown-item" (click)="deleteClient(client.cpf)"
                  >Remover Cliente</a
                >
              </li>
            </ul>
          </div>
        </td>
      </tr>
    </tbody>
  </table>
</div>
